export const $=t=>document.getElementById(t)||document.querySelector(t);export const $html=t=>{return(new DOMParser).parseFromString(t,"text/html").body.childNodes[0]};export const $if=(t,o)=>o?t?o:"":t||"";export const getRandomInt=(t,o)=>(t=Math.ceil(t),o=Math.floor(o),Math.floor(Math.random()*(o-t+1))+t);export const getRandomArrayVal=t=>t[getRandomInt(0,t.length-1)];export const getRandomRotationByInterval=t=>0===t?0:getRandomInt(0,360/t)*t;export const rollDicePct=t=>getRandomInt(0,100)<=t;